echo "src/gz custom_libernet https://raw.githubusercontent.com/NevermoreSSH/openwrt-packages2/main/libernetplus" | tee -a /etc/opkg/customfeeds.conf;opkg update;opkg install luci-app-libernet-plus;uci set network.libernet=interface;uci set network.libernet.proto='none';uci set network.libernet.ifname='tun1';uci commit;uci add firewall zone;uci set firewall.@zone[-1].network='libernet';uci set firewall.@zone[-1].name='libernet';uci set firewall.@zone[-1].masq='1';uci set firewall.@zone[-1].mtu_fix='1';uci set firewall.@zone[-1].input='REJECT';uci set firewall.@zone[-1].forward='REJECT';uci set firewall.@zone[-1].output='ACCEPT';uci commit;uci add firewall forwarding;uci set firewall.@forwarding[-1].src='lan';uci set firewall.@forwarding[-1].dest='libernet';uci commit;/etc/init.d/network restart;reboot